import "./index.css";
import Frontpage from "./Components/Frontpage";
import Accountpage from "./Components/Accountpage";

import { Routes, Route, Navigate, useParams } from "react-router-dom";
import { useSelector,useDispatch} from "react-redux";

//Assignment 2

function Protected(props) {
  return props.loggedIn ? props.children : <Navigate to={"/"} replace={true} />;
}

function App() {
  const dispatch = useDispatch();

  const { isLogin } = useSelector((state) => {
    return state.frontpage;
  });
  const {language } = useSelector((state)=>{
    console.log(state,state.frontpage.language)
    return state.frontpage;
  })

  return (
    <div className="App">

      <Routes>
        <Route path="/" element={<Frontpage/>} /> 
        <Route path="/member/:zh" language={dispatch(language("zh"))} />
        <Route path="/member/:eng" language={dispatch(language("en"))}  />


        <Route path="/member" element={
            <Protected loggedIn={isLogin}>
              <Accountpage />
            </Protected>
            }
        ></Route>

      </Routes>
    </div>
  );

}

export default App;
