import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  language :"eng",
  chinese:{
  lang:"zh",
  login :"登入 ",
  account:"用戶Id",
  password:"密碼",
  Register:"注冊",
  name:"注冊名稱",
  confirm:"確認",
  topup:"增值",
  Transfer:"轉帳",
  balance:"餘額"
  },
English:{
    lang:"en",
    login :"login ",
    account:"account",
    password:"password",
    Register:"Register",
    name:"name",
    confirm:"confirm",
    topup:"topup",
    Transfer:"Transfer",
    balance:"balance"
  }
};

export const languagesSlice = createSlice({
  name: "languages",
  initialState: { ...initialState },
  reducers: {
    setLanguage:function (state,action) {
      state.language = action.payload; 
    }
  },
});


export const {
  setLanguage,
} = languagesSlice.actions;

export default languagesSlice.reducer;
