import React from "react";
import PageStyle from "../css/Page.module.css";
import token from "./Frontpage";
// import LogoutLogo from "../img/LOGOUT.png";
import { useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux"; 

import {setLogout}from "../Slice/loginSlice"

import {inputtopupMoney} from "../Slice/topupSlice"



const Accountpage = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();


  const logout = () => {
    dispatch(setLogout())
    navigate("/");
  };

  const topup = () => {
    fetch("/member/topup", {
      headers: { "Content-Type": "application/json" },
      method: "POST",
      body: JSON.stringify({
        // token: loginPassword,      
        money: inputtopupMoney,
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data) {
          console.log(data, "get");
        } else {
          console.log(data, "error");
          
        }
      });
  };

  const transfer = () => {
    fetch("/member/transfer", {
      headers: { "Content-Type": "application/json" },
      method: "POST",
      body: JSON.stringify({
          // token:
          // to:
          // money:
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        if (data) {
          console.log(data, "get");
        } else {
          console.log(data, "error");

        }
      });
  };

  // const profile = () => {
  //   fetch("/member/register", {
  //     headers: { "Content-Type": "application/json" },
  //     method: "POST",
  //     body: JSON.stringify({
        
  //     }),
  //   })
  //     .then((res) => res.json())
  //     .then((data) => {
  //       if (data) {
  //         console.log(data, "get");
  //       } else {
  //         //error
  //       }
  //     });
  // };


  return (
    <div className={PageStyle.pageWrapper}>
      <div className={PageStyle.memberPageTop}>
        <h1 className={PageStyle.pageTitle}>HI ,</h1>
        <span className={PageStyle.logoutLogo}>
          <button className={PageStyle.logoutButton} onClick={logout}>
            logout
          </button>
        </span>
      </div>

      {/* <span class="balanceBox">
        <p class="balanceTitle">Balance</p>
        <p class="balanceTitle">$</p>
      </span> </div> */}

      <div className={PageStyle.memberBox}>
        <div className={PageStyle.topUpBox}>
          <h1>Top Up</h1>
          <div className={PageStyle.inputBox}>
            <input
              type="text"
              placeholder="amount"
              onChange={(e) => {
                dispatch(inputtopupMoney(e.target.value));
                
              }}
              required
            />
            {/* <span>amount</span> */}
          </div>
          <button className={PageStyle.button} onClick={topup}>
            Comfirm
          </button>
        </div>

        <div className={PageStyle.TransferBox}>
          <h1>Transfer</h1>
          <div className={PageStyle.inputBox}>
            <input type="text" placeholder="account ID" required />
            {/* <span>account ID</span> */}
          </div>
          <div className={PageStyle.inputBox}>
            <input type="text" placeholder="amount" required />
            {/* <span>amount</span> */}
          </div>
          <button className={PageStyle.button} onClick={transfer}>
            Comfirm
          </button>
        </div>

        <div>
          <h1 className={PageStyle.boxTitle}>Profile</h1>
          <div className={PageStyle.inputBox}>
            <p>ID </p>
            <p>Name</p>
            <p>Balance</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Accountpage;
