import "./index.css";
import Frontpage from "./Components/Frontpage";
import Accountpage from "./Components/Accountpage";
import FrontpageCH from "./Components/FrontpageCH";
import AccountpageCH from "./Components/AccountpageCH";

import { Routes, Route, Navigate, useParams } from "react-router-dom";
import { useSelector } from "react-redux";

//Assignment 2

function FrontCH() {
  const { language } = useParams();
  if (language === "ch" || language === "chinese" || language==="CH") {
    return (
      <div>
        <FrontpageCH />
      </div>
    );
  }
}
function AccountCH() {
  const { language } = useParams();
  if (language === "ch" && "chinese"&&"CH") {
    return (
      <div>
        <AccountpageCH />
      </div>
    );
  }
}

function Protected(props) {
  return props.loggedIn ? props.children : <Navigate to={"/"} replace={true} />;
}

function App() {
  const { isLogin } = useSelector((state) => {
    return state.login;
  });

  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<Frontpage />} />
        <Route path="/:language" element={<FrontCH />} />
        <Route path="/member/:language" element={
          <Protected loggedIn={isLogin}>
            <AccountCH/> 
          </Protected>
        } />


        <Route
          path="/member"
          element={
            <Protected loggedIn={isLogin}>
              <Accountpage />
            </Protected>}
        ></Route>
      </Routes>
    </div>
  );

}

export default App;
