import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  language :"eng",
        account:"account",
        password:"password",
        Register:"Register",
        name:"name",
        confirm:"confirm",
        topup:"topup",
        Transfer:"Transfer",
        balance:"balance"
};

export const languagesSlice = createSlice({
  name: "languages",
  initialState: { ...initialState },
  reducers: {
    setLanguage:function (state,action) {
      state.language = action.payload; 
    }
  },
});


export const {
  setLanguage,
} = languagesSlice.actions;

export default languagesSlice.reducer;
